arguments.argument:
if ownerPlayer.class != CLASS_ENGINEER || team != ownerPlayer.team || collision_circle(x,y+10,25,Sentry,false,true)>=0 instance_destroy();
if built == 0 && vspeed == 0 {
    if hp < maxHp hp+=1;
    else if hp == maxHp built = 1;
    image_index = floor(hp/maxHp*10);
    if landed == 0 {
        landed = 1;
        playsound(x,y,SentryFloorSnd);
        playsound(x,y,SentryBuildSnd);
    }  
}
if (built == 1 && currentWeapon == -1){
    image_index=11;
    image_speed=0;
    hp=maxHp;
    currentWeapon = instance_create(x,y,weapons[0]);
    currentWeapon.ownerPlayer=ownerPlayer;
    currentWeapon.team=team;
    currentWeapon.startDirection=startDirection;
    if startDirection == 1 currentWeapon.direction=0;
    else if startDirection == -1 currentWeapon.direction=180;
}


if(place_free(x,y+1)) {
    vspeed += 0.6;
}

if(vspeed>10) {
    vspeed=10;
}
