arguments.argument:
{
    if tracking == false {
       tracking = true;
       if !instance_exists(NoticeO) instance_create(0,0,NoticeO);
       with NoticeO notice = NOTICE_PLAYERTRACKENABLE;
    }
    track_id += 1;
    if track_id > ds_list_size(global.players) -1 {
        track_id = 0;
    }
    player = ds_list_find_value(global.players, track_id);
}
