if(visible) {
    if(fadeout) {
        bubbleAlpha -= 0.05;
        if(bubbleAlpha <= 0) {
            bubbleAlpha = 0;
            visible = false;
            fadeout = false;
        }
    }
    x = round(owner.x)+10;
