arguments.argument:
{
    xoffset = view_xview[0];
    yoffset = view_yview[0];
    xsize = view_wview[0];
    ysize = view_hview[0];

    if(mouse_x > xoffset+xbegin and mouse_x < xoffset+xbegin+width) {
        virtualitem = round((mouse_y-ybegin-yoffset)/spacing);
        if(mouse_check_button_pressed(mb_left)){
            if(virtualitem >= 0 and virtualitem < items) {
                event_user(virtualitem);
            }
        }
    } else {
        virtualitem = -1;
    }
}
