arguments.argument:
if editing == 0 {
    global.playerName = string_copy(global.playerName, 0, min(string_length(global.playerName), MAX_PLAYERNAME_LENGTH));
    if string_count("#",global.playerName) > 0 {
        show_message("Invalid character '\#' in name");
        global.playerName = "Player";
        value[0] = global.playerName;
    }
    ini_open("ggc.ini");
    ini_write_string("Settings", "PlayerName", global.playerName);
    ini_close();
    editing = -1;
}
