arguments.argument:
{
    global.particles = (global.particles+2) mod 3;

    ini_open("ggc.ini");
    ini_write_real("Settings", "Particles", global.particles);
    ini_close();

    if(global.particles == PARTICLES_NORMAL) {
        value[3] = "Normal";
    } else if(global.particles == PARTICLES_ALTERNATIVE) {
        value[3] = "Alternative (faster)";
    } else {
        value[3] = "Disabled";
    }
}
