arguments.argument:
switch (global.gibLevel){
    case(0): 
        global.gibLevel = 1;
        value[4] = "1, Blood only"; 
        break;
    case(1): 
        global.gibLevel = 2;
        value[4] = "2, Blood and medium gibs";
        break;
    case(2): 
        global.gibLevel = 3;
        value[4] = string(global.gibLevel) + ", Full blood and gibs";
        break;
    default: 
        global.gibLevel = 0;
        value[4] = "0, No blood or gibs";
        break;
        }

ini_open("ggc.ini");
ini_write_real("Settings", "Gib Level", global.gibLevel);
ini_close();
