arguments.argument:
{
    if global.showHealer == 1 global.showHealer = 0;
    else global.showHealer = 1;

    ini_open("ggc.ini");
    ini_write_real("Settings", "Show Healer", global.showHealer);
    ini_close();

    if(global.showHealer == 0) {
        value[6] = "Disabled";
    } else {
        value[6] = "Enabled";
    }
}
