arguments.argument:
{
    if global.showHealing == 1 global.showHealing = 0;
    else global.showHealing = 1;

    ini_open("ggc.ini");
    ini_write_real("Settings", "Show Healing", global.showHealing);
    ini_close();

    if(global.showHealing == 0) {
        value[7] = "Disabled";
    } else {
        value[7] = "Enabled";
    }
}
