arguments.argument:
{
    if global.killCam == 1 global.killCam = 0;
    else global.killCam = 1;

    ini_open("ggc.ini");
    ini_write_real("Settings", "Kill Cam", global.killCam);
    ini_close();

    if(global.killCam == 0) {
        value[9] = "Disabled";
    } else {
        value[9] = "Enabled";
    }
}
