arguments.argument:
done=true;
if(global.myself.object == -1) exit;

if global.myself.sentry != -1 {
    if instance_exists(NoticeO) with NoticeO instance_destroy();
    instance_create(0,0,NoticeO);
    with NoticeO notice = NOTICE_AUTOGUNEXISTS;
}
else if global.myself.object.nutsNBolts < 100 {
    if instance_exists(NoticeO) with NoticeO instance_destroy();
    instance_create(0,0,NoticeO);
    with NoticeO notice = NOTICE_NUTSNBOLTS;
}
else if collision_circle(global.myself.object.x,global.myself.object.y,50,Sentry,false,true)>=0 {
    if instance_exists(NoticeO) with NoticeO instance_destroy();
    instance_create(0,0,NoticeO);
    with NoticeO notice = NOTICE_TOOCLOSE;
}
else if (global.myself.quickspawn != 1 && global.myself.object.nutsNBolts == 100 && collision_circle(global.myself.object.x,global.myself.object.y,50,Sentry,false,true)<0)
{
    clearbuffer(global.sendBuffer);
    writebyte(BUILD_SENTRY,global.sendBuffer);
    sendmessage(global.serverSocket, 0, 0, global.sendBuffer);
}
