arguments.argument:
if global.winners == TEAM_BLUE {
    teamoffset=1;
    if global.myself.team == TEAM_RED {
        sound=FailureSnd;
        global.myself.humiliated = 1;
        }
    else sound = VictorySnd;
    }
else if global.winners == TEAM_RED {
    teamoffset = 0;
    if global.myself.team == TEAM_BLUE {
        sound=FailureSnd;
        global.myself.humiliated = 1;
        }
    else sound = VictorySnd;
    }
else if global.winners == TEAM_SPECTATOR{
    teamoffset = 2;
    sound=FailureSnd;
    global.myself.humiliated = 1;
    }
if !instance_exists(ArenaHUD) AudioControlPlaySong(sound, false);
