arguments.argument:
{
    if !bounced { explosionDamage-=2 knockback-=1 }
 //   if bounced and !fuse { alarm[0]=50 fuse=true }
    var ospeed;
    bounced=true;
    move_bounce_solid(1);
    ospeed = vspeed;
    speed*=0.45
    if abs(speed)<1 then speed=0;
    if vspeed<ospeed then rotspeed*=-0.8 else rotspeed*=0.8;
}
