arguments.argument:
{
    if(readyToShoot and bubbleCount < 25) {
        var shot;
        randomize();
            shot = instance_create(x+lengthdir_x(8,owner.aimDirection),y+lengthdir_y(8,owner.aimDirection),Bubble);
            shot.direction=owner.aimDirection+(random(15)-7);
            shot.speed=10+random(2);
            shot.owner=owner;
            shot.ownerPlayer=ownerPlayer;
            shot.team=owner.team;
            shot.gun = id;
            shot.weapon=WEAPON_BLADE;
            with(shot) {
                motion_add(owner.direction, owner.speed);
            }
        justShot=true;
        readyToShoot=false;
        alarm[0]=reloadTime-random(3);
        bubbleCount+=1;
    }
}
